import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/services/api.service';
import mdlPoderes from 'src/models/mdlPoderes';

@Component({
  selector: 'app-detalhes',
  templateUrl: './detalhes.component.html',
  styleUrls: ['./detalhes.component.css']
})
export class DetalhesComponentPoderes {

  poderes?: mdlPoderes;

  constructor(private ApiService: ApiService, private route: ActivatedRoute, private router: Router) {

  }

  ngOnInit(){
    const id = this.route.snapshot.paramMap.get('id')
    this.ApiService.getIdPoderes(id).subscribe(poderes => {
      this.poderes = poderes
    })

  }

  remover(): void {
    const id = this.route.snapshot.paramMap.get('id')
    this.ApiService.deletePoderes(id).subscribe(() => {
      this.router.navigate(['/poderes'])
    })
  }

  navegarUpdate(): void {
    this.router.navigate(['/poderes/update'])
  }

  Voltar(): void {
    this.router.navigate(['/poderes'])
  }
}
<div class="col-12 d-flex justify-content-center">
    <div class="col-5 card text-center my-4 p-4">
        <h2>Detalhes</h2>
    </div>
</div>
<div class="col-12 d-flex justify-content-center align-items-center">
    <div class="card col-5">
        <div class="card-body text-center">
            <h5 class="card-title">Heroi</h5>
            <p class="card-text">Nome: {{poderes?.superPoder}}</p>
            <p class="card-text">Heroi: {{poderes?.descricao}}</p>
            <div class="d-flex justify-content-around">
                <a class="btn-success btn text-white">Poderes</a>
                <a class="btn-primary btn" routerLink="poderes/update/{{poderes?.id}}">Alterar</a>
                <a class="btn-danger btn" (click)="remover()">Excluir</a>
                <a class="btn-info btn text-white" (click)="Voltar()">Voltar</a>
            </div>
        </div>
    </div>
</div>
