import { ApiService } from 'src/app/services/api.service';
import { Component } from '@angular/core';
import mdlHeroisPoderes from 'src/models/mdlHeroisPoderes';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-poder',
  templateUrl: './add-poder.component.html',
  styleUrls: ['./add-poder.component.css']
})
export class AddPoderComponent {
  heroiPoderes: mdlHeroisPoderes[] = []
  heroiPoder: mdlHeroisPoderes = {
    heroiId: 0,
    superPoderId: 0
  }

  resposta?: boolean

  constructor(private ApiService: ApiService, private router: Router) {

  }

  criar(): void {
    this.ApiService.postHerois(this.herois).subscribe(() => {
      this.resposta = true;
      setTimeout(()=>{
        this.router.navigate(['/herois'])
      }, 2000)
    }, (erro)=> {
      this.resposta = true;
      setTimeout(()=>{
        this.router.navigate(['/herois'])
      }, 2000)
    })
  }

  Voltar(): void {
    this.router.navigate(['/herois'])
  }
}
