import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="px-4 py-5 my-5 text-center">
    <h1 class="display-5 fw-bold text-body-emphasis">Gerenciamento de Quartel de Heróis</h1>
    <div class="col-lg-6 mx-auto">
      <p class="lead mb-4">Projete e personalize rapidamente um site responsivo para gerenciar o quartel de heróis usando o Bootstrap, o toolkit de código-fonte aberto para a interface do usuário mais popular do mundo. Ele inclui variáveis e mixins Sass, um sistema de grade responsivo, componentes pré-construídos extensos e poderosos plugins JavaScript.</p>
      <div class="d-grid gap-2 d-sm-flex justify-content-sm-center">
        <button type="button" class="btn btn-primary btn-lg px-4 gap-3">Botão Principal</button>
        <button type="button" class="btn btn-outline-secondary btn-lg px-4">Secundário</button>
      </div>
    </div>
</div>
