import { ApiService } from './../../../services/api.service';
import mdlHerois from 'src/models/mdlHerois';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-detalhes',
  templateUrl: './detalhes.component.html',
  styleUrls: ['./detalhes.component.css']
})
export class DetalhesComponent {

  herois?: mdlHerois;

  constructor(private ApiService: ApiService, private route: ActivatedRoute, private router: Router) {

  }

  ngOnInit(){
    const id = this.route.snapshot.paramMap.get('id')
    this.ApiService.getIdHerois(id).subscribe(heroi => {
      this.herois = heroi
    })

  }

  remover(): void {
    const id = this.route.snapshot.paramMap.get('id')
    this.ApiService.deleteHerois(id).subscribe(() => {
      this.router.navigate(['/herois'])
    })
  }

  navegarUpdate(): void {
    this.router.navigate(['/herois/update'])
  }
}
<div class="col-12 d-flex justify-content-center align-items-center">
    <div class="card" style="width: 18rem;">
        <div class="card-body text-center">
            <h5 class="card-title">Heroi</h5>
            <p class="card-text">Nome: {{herois?.nome}}</p>
            <p class="card-text">Heroi: {{herois?.nomeHeroi}}</p>
            <p class="card-text">Nascimento: {{herois?.dataNascimento}}</p>
            <p class="card-text">Altura: {{herois?.altura}}</p>
            <p class="card-text">Peso: {{herois?.peso}}</p>
            <div class="d-flex justify-content-around">
                <a class="btn-warning btn">Adcionar Poder</a>
                <a class="btn-primary btn" routerLink="/herois/update/{{herois?.id}}">Alterar</a>
                <a class="btn-danger btn" (click)="remover()">Excluir</a>
            </div>
        </div>
    </div>
</div>
